import React, { useEffect, useState } from 'react'

 const useGetFetch = (url) => {
    const [data, setData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
  
    useEffect(() =>{
      fetch(url, {method:'GET'})
       .then(response => {             //gets data
          if(!response.ok){            //if cant fetch/resource is faulty
            throw Error('could not fetch');
          }
         return response.json();       //turns data in to a readable format
       })
       .then(data => {                 // takes data and saves it as a state           
        setLoading(false);
        setData(data);
        setError(null);
       })   
       .catch((error) => {            // catches and displays error
        setLoading(false);
        setError(error.massage);
       })
      
     }, [url]);  

     return { data, loading, error };

    }
export default getFetch;
