import React from 'react'
import "./css/room.css";
import useGetFetch from './fetch/useGetFetch'
import { useNavigate } from "react-router-dom"
import { createRoom } from "./Controller/RoomController";
import { useEffect, useState } from "react";


class Room extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
        customers: []
    }
}
componentDidMount() {
    fetch("https://raw.githubusercontent.com/aspsnippets/test/master/Customers.json")
    .then(res => res.json())
    .then(
        (customers) => {
            this.setState({ customers: customers });
        },
        (error) => {
            alert(error);
        }
    )
}
   render(){

  return (
    <body>
      <div class="home">
        <div class="title_text">
          <h1 class="title">Treten Sie einer vorhandenen Watchparty bei</h1>
        </div>
        <div class="roomlist">
        <tbody>
                {this.state.customers.map(customer =>
                    <tr>
                        <td>{customer.CustomerId}</td>
                        <td>{customer.Name}</td>
                        <td>{customer.Country}</td>
                    </tr>
                )}
            </tbody>
        </div>
        <div class="welcome_text">
          <p class="textbox">Oder vielleicht doch eine eigene Watchparty erstellen?</p>
          <button onClick={event => handleButton()} className="homescreen_buttons">Einen neuen Raum erstellen</button>
        </div>
      </div>
    </body>
  )
}}
export default Room;
