import React from 'react'
import "./css/room.css";
import useGetFetch from './fetch/useGetFetch'
import { useNavigate } from "react-router-dom"
import { createRoom } from "./Controller/RoomController";
import { useEffect, useState } from "react";


const Room = () => {
  const navigate = useNavigate()
  const handleButton = () => {		// gives button its funktion
    if (sessionStorage.getItem('id') == null) {
      navigate('/UserCreateSide')
    }
    else {
      createRoom()
			setTimeout(function() {
				navigate('/Watchparty')
			}, 500)
		}
	}
  const rooms = ['raum','raim']
  const [data, getData] = useState([])
  const URL = 'https://gruppe18.toni-barth.com/rooms/';

  useEffect(() => {
    fetchData()
  }, [])


  const fetchData = () => {
    fetch(URL)
      .then((res) =>
        res.json())

      .then((response) => {
        console.log(response);
        getData(response);
      })
      
  }
  

  return (
    <body>
     <div className="home">
      {data.map(room => ( <h2>{ room.name }</h2>))}
    </div>
    </body>
  )
}
export default Room;
