import React from 'react'
import "./css/room.css";
import useGetFetch from './fetch/useGetFetch'
import { useNavigate } from "react-router-dom"
import { createRoom } from "./Controller/RoomController";
import { useEffect, useState } from "react";



const Room = () => {
  const navigate = useNavigate()
  const handleButton = () => {		// gives button its funktion
    if (sessionStorage.getItem('id') == null) {
      navigate('/UserCreateSide')
    }
    else {
      createRoom()
      navigate('/Watchparty')
    }
  }

  const Table = ({ data = [] }) => {
    return (
      <table>
        {
          !(data.length)
            ? <tr><td>Nothing to show!</td></tr>
            : data.map((row) =>
              <tr>
                {Object.values(row).map((cell) => <td>{cell}</td>)}
              </tr>)
        }
      </table>
    )
  }
  const URL = ('https://gruppe18.toni-barth.com/rooms/');
  const [data, setData] = React.useState([])

  const go = async () => {
    await fakeDelay()
    setData(await (await fetch(URL)).json())
  }





  return (
    <body>
      <div class="home">
        <div class="title_text">
          <h1 class="title">Treten Sie einer vorhandenen Watchparty bei</h1>
        </div>
        <div class="roomlist">
          {data.map((rooms) => (
            <div className='rooms' key={rooms.name}>
              <h4>{rooms.name}</h4>
            </div>
          ))}
        </div>
        <div><button onClick={go}>Fetch data for table</button> <Table data={data} /></div>
        <div class="welcome_text">
          <p class="textbox">Oder vielleicht doch eine eigene Watchparty erstellen?</p>
          <button onClick={event => handleButton()} className="homescreen_buttons">Einen neuen Raum erstellen</button>
        </div>
      </div>
    </body>
  )
}
export default Room;
