import "./css/room.css";

import React, { Component } from 'react';


class Room extends Component{
  constructor(props) {
    super(props)   
    this.state = {
        records: []
    }
     
}

componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
        .then(response => response.json())
        .then(records => {
            this.setState({
                records: records
            })
        })
        .catch(error => console.log(error))
}

renderListing() {
    let recordList = []
    this.state.records.map(record => {
        return recordList.push(`<li key={record.id}>{record.name}</li>`)
    })

    return recordList;
}
render(){
  return (
    <body>
      <div class="home">
        <div class="title_text">
          <h1 class="title">Treten Sie einer vorhandenen Watchparty bei</h1>
        </div>
        <div class="roomlist">
        <ul>
                {this.renderListing()}
            </ul>`
        </div>
        <div class="welcome_text">
          <p class="textbox">Oder vielleicht doch eine eigene Watchparty erstellen?</p>
          <button onClick={event => handleButton()} className="homescreen_buttons">Einen neuen Raum erstellen</button>
        </div>
      </div>
    </body>
  )
}}
export default Room;
