import React from 'react'
import "./css/room.css";
import useGetFetch from './fetch/useGetFetch'
import { useNavigate } from "react-router-dom"
import { createRoom } from "./Controller/RoomController";
import { useEffect, useState } from "react";


class Room extends React.component{

  constructor(props) {
    super(props);
    this.state = {
      employee: []
    }
  }
  componentDidMount() {
    fetch('https://gruppe18.toni-barth.com/rooms/').then((res) => res.json())
      .then((data) => {
        console.log(data)

        this.setState({ employee: JSON.stringify(data) }, () => {
          alert(this.state.hugeText);
        });
      })
  }
  render(){
  const emp = this.state.employee
  return (
    
    <body>
      <div class="home">
        <div class="title_text">
          <h1 class="title">Treten Sie einer vorhandenen Watchparty bei</h1>
        </div>
        <div class="roomlist">
        <ul>
                {emp.map((record, i) => <li key={i}>{record.id} - {record.firstName} {record.firstName}</li>)}
            </ul>
        </div>
        <div class="welcome_text">
          <p class="textbox">Oder vielleicht doch eine eigene Watchparty erstellen?</p>
          <button onClick={event => handleButton()} className="homescreen_buttons">Einen neuen Raum erstellen</button>
        </div>
      </div>
    </body>
  )


}}
export default Room;
