import "./css/room.css";

import React, { Component } from 'react';


class Listing extends Component {
    constructor(props) {
        super(props)
        this.state = {
            rooms: []
        }

    }

    componentDidMount() {
        fetch('https://gruppe18.toni-barth.com/rooms/')
            .then(response => response.json())
            .then(rooms => {
                this.setState({
                    rooms: rooms
                })
            })
            .catch(error => console.log(error))
    }

    renderListing() {
        let roomList = []
        this.state.rooms.map(rooms => {
            return roomList.push(<li key={rooms.id}>{rooms.name}</li>)
        })

        return roomList;
    }
    render() {
        return (
            <ul>
                {this.renderListing()}
            </ul>
        );
    }
}
export default Listing;
