	const [input, setInput] = useState('')
	const handleButton = (input) => {		// gives button its funktion
		//submit user
		//const user = createUser(input)
		// then(window.location.href = '/Watchparty') 
		console.log(input)
	}
	return (
		<>
			<body class="home">
				<div>
					<div class="hosttitle_text">
						<h1 class="hosttitle">Erstellen sie einen eigenen Raum f√ºr ihre Watchparty!</h1>
					</div>
					<div class="host_text">
						<p class="host_textbox">Bitte geben Sie einen Benutzernamen ein um einen Raum zu erstellen.</p>
					</div>
                    <div class="input">
                        <div class="eins">
                        <input type="text" n="roomname" class="input_box" placeholder="Benutzernamen eingeben zb.: Floppa" value={input} onChange={(change) => setInput(input.target.value)}></input>
						</div>
						<div class="zwei">
						<button onClick={event => handleButton(input)} className="host_button">Starten	</button>
                        </div>
                    </div>
				</div>
			</body>
		</>
	);
};

export default Host;
