import React, { useState, useEffect } from 'react';
import "./css/watchparty.css";
import {useNavigate} from "react-router-dom"
import { leaveRoom } from './Controller/RoomController';
import ReactPlayer from 'react-player';
import { getVideo, postVideo } from './Controller/VideoController';

const Watchparty = () => {		// room siplay with userlist of rpp
	const [link, setLink] = useState('')
	const [url, setUrl] = useState('https://www.youtube.com/watch?v=Q0B5dLHDQ2w')
	const navigate = useNavigate()
	const unsername= sessionStorage.getItem('name')
	const id = sessionStorage.getItem('id')
	const roomname = sessionStorage.getItem('roomname')
	const [intervalId, setIntervalId] = useState(null);
	const [sync, setSnyc]= useState(true)

	useEffect(() =>{
		if(sync=true)
			{
				makeTimer()
			}
		id
	  }, []);
	  
	const makeTimer =() =>{
		if(intervalId==null)
		{
			const interval = setInterval(() => {
				console.log('test')
				setIntervalId(interval)
			  }, 1000)
		}
		else{
			console.log('test')
		}
		}
	
	


	const handleButton = () => {		// gives button its funktion leave, submit video
		setUrl(link)
		postVideo(url)
		console.log(url)
	}
	const handleButton2 = () => {		// gives button its funktion, leave Room
		leaveRoom(sessionStorage.getItem('roomname'))
		clearInterval(intervalId)
		navigate('/Room')
	}

	const firstLoad = () => {		// loads a standart video, when romm is new, when user joins existing Room, the room should update
		getVideo().then(res => {
			if(res==null || res=='')
			{
				setUrl('https://www.youtube.com/watch?v=Q0B5dLHDQ2w') 
			}

			else{
				setUrl(res)
			}
		})}
	return (
		<>
			<body class="home">
				<div>
					<div class="partytitle_text">
						<h1 class="party_title">Raum: {sessionStorage.getItem('roomname')}</h1>
					</div>
					<div class="player">
						<ReactPlayer controls url={url} width={1280} height={720}/>
						
						<p class="users">Nutzer in dieser Watchparty:</p>
						
					</div>
					<div class="links">
						<input type="text" name="roomname" class="link_box" placeholder="Link einfÃ¼gen" value={link} onChange={(change) => setLink(change.target.value)}></input>
						<button onClick={event => handleButton()} className="link_submit">Starten	</button>
						<button onClick={event => handleButton2()} className="link_leave">Raum Verlassen</button>
					</div>
					
				</div>
			</body>
		</>
	);
};

export default Watchparty
